select tbw.poverty_people_id as poverty_people_id, sum(tbw.id) as tSum from t_buss_wish tbw left join t_poverty_people tpp on tbw.poverty_people_id = tpp.id where tpp.status <> '0' and tbw.wish_status = '0'
and tpp.village_id in
#{item}
and tpp.dept_id in
#{item}
group by tbw.poverty_people_id

select tbw.id, tpp.poverty_name as poverty_name, tbw.wish_title, tbw.wish_content, tbw.wish_status, tbw.wish_create_date, tbw.wish_finish_date, tbw.realize_name from t_buss_wish tbw left join t_poverty_people tpp on tbw.poverty_people_id = tpp.id where 1=1
and tbw.poverty_people_id = #{tBussWishVO.povertyPeopleId}
AND ( tbw.wish_title like CONCAT('%',#{tBussWishVO.wishTitle},'%') OR tbw.wish_content like CONCAT('%',#{tBussWishVO.wishContent},'%') )
order by tbw.create_time desc limit #{page.minNum},#{page.pageSize}

select count(tbw.id) from t_buss_wish tbw left join t_poverty_people tpp on tbw.poverty_people_id = tpp.id where 1=1
and tbw.poverty_people_id = #{tBussWishVO.povertyPeopleId}
AND ( tbw.wish_title like CONCAT('%',#{tBussWishVO.wishTitle},'%') OR tbw.wish_content like CONCAT('%',#{tBussWishVO.wishContent},'%') )
